import sailpoint.connector.DelimitedFileConnector;


Map map = DelimitedFileConnector.defaultBuildMap(cols, record);

String employeeStatus = (String)map.get("employeeStatus");

//TRUE  => Employee working
//FALSE => Employee vacation
//!Boolean.valueOf(employeeStatus)

if(employeeStatus != null){  
   map.put("IIQDisabled",!Boolean.valueOf(employeeStatus));
}
return map;
